import React, { createContext, useContext, useState } from 'react'
import axios from 'axios';


const URL="http://localhost:5077/api/v1/";


const GlobalContext = React.createContext()

export const GlobalProvider=({children})=>{

const [incomes,setIncomes]=useState([])
const [expenses,setExpenses]=useState([])
const [error,setError]=useState(null)

const addIncome = async(income)=>{ 
    try {
        const response = await axios.post(`${URL}add-income`, income);
        getIncomes();
    } catch (error) {
        if (error.response) {
            if (error.response.data && error.response.data.message) {
                setError(error.response.data.message);
            } else if (error.response.data) {
                setError(error.response.data);
            } else {
                setError(error.response.statusText || "An unknown error occurred.");
            }
        } else if (error.request) {
            setError("No response received from the server.");
        } else {
            setError("Error setting up the request: " + error.message);
        }
    }
};

const getIncomes = async () => {
    const response = await axios.get(`${URL}get-incomes`)
    setIncomes(response.data)
    console.log(response.data)
}
const deleteIncome = async (id) => {
    const res  = await axios.delete(`${URL}delete-income/${id}`)
    getIncomes()
}
const totalIncome = () => {
    let totalIncome = 0;
    incomes.forEach((income) =>{
        totalIncome = totalIncome + income.amount
    })

    return totalIncome;
}
const addExpense = async (income) => {
    const response = await axios.post(`${URL}add-expense`, income)
        .catch((err) =>{
            setError(err.response.data.message)
        })
    getExpenses()
}

const getExpenses = async () => {
    const response = await axios.get(`${URL}get-expenses`)
    setExpenses(response.data)
    console.log(response.data)
}

const deleteExpense = async (id) => {
    const res  = await axios.delete(`${URL}delete-expense/${id}`)
    getExpenses()
}

const totalExpenses = () => {
    let totalIncome = 0;
    expenses.forEach((income) =>{
        totalIncome = totalIncome + income.amount
    })

    return totalIncome;
}
console.log(totalIncome())
    return (
        <GlobalContext.Provider value={{
            addIncome,
            getIncomes,
            incomes,
            deleteIncome,
            totalIncome,
            expenses,
            addExpense,
            getExpenses,
            deleteExpense,
            totalExpenses
        }}>
            {children}
        </GlobalContext.Provider>
    );

}

export const useGlobalContext=()=>{
    return useContext(GlobalContext)
}
